//for 旅館照片幻燈片設置

$(document).ready(function(){
    $('.slider-info').slick({
        dots: true,  // 顯示點點導航
        infinite: true,  // 無限循環播放
        speed: 500,  // 幻燈片切換速度
        slidesToShow: 1,  // 每次顯示一張圖片
        adaptiveHeight: true,  // 自適應高度
        arrows: true,  // 前後箭頭導航
        autoplay: true,  // 自動播放
        autoplaySpeed: 2000  // 自動播放速度 (2秒)
    });
});


